package lecture007;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name = "TADEO";
		switch (name) {
		case "JUAN": {
			System.out.println("HI " + name);
		break;
		}
		case "PEDRO": {
			System.out.println("HI " + name);
		break;
		}
		default:
			break;
		}
	}

}
